mapper.select:
select * ,u.is_identified as identified
from tb_user u,
tb_user_info ui
where
u.user_info_id = ui.id
and u.is_deleted = 0
and u.id=#{id}
and ui.gender=#{gender}
and u.phone=#{phone}
and u.is_identified=#{identified}


        and u.student_id=#{studentId}

    and u.school_id=#{schoolId}

        and u.deptId=#{deptId}

            and u.clazzId=#{clazzId}



and ui.name like #{name};
